package pacman;import javax.microedition.lcdui.*;public class GridNode{		public static final int NO_PELLET = 0;	public static final int SMALL_PELLET = 1;		public static final int BIG_PELLET = 2;		private static int size_;		public static void setSize(int i)	{		size_ = i;	}	public static int getSize()	{		return size_;	}	private int pellet_;	private GridNode left_;			private GridNode right_;		private GridNode up_;			private GridNode down_;	private int posX_;			 	private int posY_;		private int pixelULX_;		private int pixelULY_;			private int pixelLRX_;		private int pixelLRY_;			private int pixelCX_;			private int pixelCY_;		public GridNode(int x, int y)	{		posX_ = x;		posY_ = y;		left_ = null;		right_ = null;		up_ = null;		down_ = null;		pellet_ = SMALL_PELLET;					pixelULX_ = posX_ * size_;		pixelULY_ = posY_ * size_;		pixelLRX_ = pixelULX_ + size_ -1;		pixelLRY_ = pixelULY_ + size_ -1;		pixelCX_ = (pixelULX_ + pixelLRX_)/2;		pixelCY_ = (pixelULY_ + pixelLRY_)/2;	}			public int pixelCenterX()	{		return pixelCX_;	}		public int pixelCenterY()	{		return pixelCY_;	}		public void setLeft(GridNode  n)	{ 		left_ = n;	}		public void setRight(GridNode n) 	{		right_ = n; 	}			public void setUp(GridNode  n)	{ 		up_ = n;	}			public void setDown(GridNode  n) { down_ = n; }			public GridNode getLeft()  { return left_; }			public GridNode getRight() { return right_; }			public GridNode getUp()    { return up_; }		public GridNode getDown()  { return down_; }		public void setPellet(int i)	{		pellet_ = i;	}			public int getPellet()	{		return pellet_;	}	public void paint(Graphics g)	{			g.setColor(255,255,255);		g.fillRect(pixelULX_,pixelULY_,pixelLRX_,pixelLRY_);		g.setColor(0,0,0);		if (left_  == null) g.drawLine(pixelULX_,pixelULY_,pixelULX_,pixelLRY_);		if (right_ == null) g.drawLine(pixelLRX_,pixelULY_,pixelLRX_,pixelLRY_);		if (up_    == null) g.drawLine(pixelULX_,pixelULY_,pixelLRX_,pixelULY_);		if (down_  == null) g.drawLine(pixelULX_,pixelLRY_,pixelLRX_,pixelLRY_);		int radius = 0;		if (pellet_ == BIG_PELLET)			radius = size_ / 3;		if (pellet_ == SMALL_PELLET)			radius = size_ / 5;		if (pellet_ != NO_PELLET)			g.fillArc(pixelCX_,pixelCY_,radius,radius,0,360);	}	}