package pacman;import javax.microedition.lcdui.*;import javax.microedition.midlet.*;public class FrameTrigger extends Thread{		GameCanvas game_;				private int frameDelay_;		private boolean stopped;		public FrameTrigger(GameCanvas g, int frameDelay)	{		game_ = g;		frameDelay_ = frameDelay;		stopped = true;	}		public void stopTrigger()	{ stopped = true; }		public void startTrigger()	{ stopped = false; }		public void run()	{				while (!stopped)		{				game_.advanceFrame();			try {                    		sleep(frameDelay_);                	} catch (InterruptedException ie) {}		}		System.out.println("FrameTrigger Thread Stopped");	}}